import { useState } from 'react';
import { Form } from 'react-router-dom';

import classes from './AuthForm.module.css';

function AuthForm() {
  const [isLogin, setIsLogin] = useState(true);
  const [password, setPassword] = useState({
    pass: '',
    confirmPass: ''
  });

  function switchAuthHandler() {
    setIsLogin((isCurrentlyLogin) => !isCurrentlyLogin);
  }

  function handlePassword(e){
    setPassword({
      ...password,
      pass: e.target.value
    })
  }

  function handleConfirmPassword(e){
    setPassword({
      ...password,
      confirmPass: e.target.value
    })
  }

  function submitHandler(event) {
    event.preventDefault();
    if(password.pass !== password.confirmPass){
      alert("Passwords do not match")
    }

  }
  return (
    <>
      <Form method="post" className={classes.form}>
        <h1>{isLogin ? 'Log in' : 'Create a new user'}</h1>
        <p>
          <label htmlFor="email">Email</label>
          <input id="email" type="email" name="email" required />
        </p>
        <p>
          <label htmlFor="image">Password</label>
          <input id="password" type="password" name="password" value={password.pass} required onChange={handlePassword}/>
        </p>
        <p>
          <label htmlFor="image">Confirm Password</label>
          <input id="password" type="password" name="password" required value={password.confirmPass} onChange={handleConfirmPassword}/>
        </p>
        <div className={classes.actions}>
          <button onClick={switchAuthHandler} type="button">
            {isLogin ? 'Create new user' : 'Login'}
          </button>
          <button type='submit' onClick={submitHandler}>Save</button>
        </div>
      </Form>
    </>
  );
}

export default AuthForm;
