import { useState } from 'react';
import { Form } from 'react-router-dom';

import classes from './AuthForm.module.css';

function AuthForm() {
  const [isLogin, setIsLogin] = useState(true);
  const [password, setPassword] = useState({
    pass: '',
    confirmPass: ''
  });

  function switchAuthHandler() {
    setIsLogin((isCurrentlyLogin) => !isCurrentlyLogin);
  }

  function handlePassword(){

  }

  // function submitHandler(event) {
  //   event.preventDefault();
  //   if(p)

  // }
  return (
    <>
      <Form method="post" className={classes.form}>
        <h1>{isLogin ? 'Log in' : 'Create a new user'}</h1>
        <p>
          <label htmlFor="email">Email</label>
          <input id="email" type="email" name="email" required />
        </p>
        <p>
          <label htmlFor="image">Password</label>
          <input id="password" type="password" name="password" value={password.pass} required onChange={handlePassword}/>
        </p>
        <p>
          <label htmlFor="image">Confirm Password</label>
          <input id="password" type="password" name="password" required  value={password.confirmPass}/>
        </p>
        <div className={classes.actions}>
          <button onClick={switchAuthHandler} type="button">
            {isLogin ? 'Create new user' : 'Login'}
          </button>
          <button>Save</button>
        </div>
      </Form>
    </>
  );
}

export default AuthForm;
